import React, { useState, useEffect } from 'react';
import authSvg from '../assets/update.svg';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import { updateUser, isAuth, getCookie, signout } from '../helpers/auth.js';

const Timeline = ({ history }) => {

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password1: '',
    textChange: 'Update',
    role: ''
  });

  const [post, setPost] = useState('');

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = () => {
    const token = getCookie('token');
    console.log(token);
    axios
    .get(`${process.env.REACT_APP_API_URL}/user/${isAuth()._id}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(res => {
      const { role, name, email } = res.data;
      setFormData({ ...formData, role, name, email });
      console.log(res);
    })
    .catch(err => {
      console.log(err.response);
      toast.error(`Error in your information: ${err.response}`);
      if(err.response === 401) {
        signout(() => {
          history.push('/login');
        });
      }
    });
  };

  const loadTimeLine = () => {

    if(formData.name !== '') {
      axios
      .get("http://localhost:8080/neon/api/all")
      .then(res => {
        console.log(res);
      });
    } else {
      toast.error('Not authorized.');
    }
  };

  const handleChange = (event) => {
    setPost(event.target.value);
    console.log
  };

  return (
    <div>
      <div>
        User: {formData.name}
      </div>
      <div>
        <button
          className='w-full max-w-xs font-bold shadow-sm rounded-lg py-3
          bg-indigo-100 text-gray-800 flex items-center justify-center transition-all duration-300 ease-in-out focus:outline-none hover:shadow focus:shadow-sm focus:shadow-outline mt-5'
          onClick={loadTimeLine}
        >
          <i className='fas fa-sign-in-alt fa 1x w-6  -ml-2 text-indigo-500' />
          <span className='ml-4'>Load Timeline</span>
        </button>
      </div>
      <div>
        <h2>
          POST
        </h2>
      </div>
      <div>
        <form>
          <input
            className='w-full px-8 py-4 rounded-lg font-medium bg-gray-100 border border-gray-200 placeholder-gray-500 text-sm focus:outline-none focus:border-gray-400 focus:bg-white mt-5'
            type='text'
            placeholder='Your Post'
            onChange={handleChange}
            value={post}
          />
        </form>
      </div>
    </div>
  )
}

export default Timeline;
